mapper.select:
select t.mac_address macAddress,s.store_name storeName,w.wifi_ip wifiIp,g.goods_name goodsName,g.goods_price goodsPrice,g.goods_specs goodsSpecs,g.goods_unit goodsUnit,t.type TYPE from tags t join wifis w on t.wifi_id = w.wifi_id join stores s on w.store_id = s.store_id join goods g on t.goods_id = g.goods_id where 1=1
and s.store_name = #{storeName}
and g.goods_name = #{goodsName}

select t.tag_id tagId,t.wifi_id wifiId,t.goods_id goodsId,t.mac_address macAddress,t.picpx picpx,t.type type
from tags t
join wifis w on t.wifi_id = w.wifi_id
where 1=1

    and w.wifi_ip =#{wifiIp}

and t.type = 1;
